mapper.select:
SELECT EXISTS(
         SELECT id
         FROM option_group
         WHERE id = #{id}
         );

SELECT EXISTS(
         SELECT id
         FROM option_group
         WHERE id = #{id}
           AND store_id = #{storeId}
         );

mapper.update:
UPDATE option_group
SET name             = #{name},
    modify_date_time = #{modifyDateTime}
WHERE id = #{id};
